INSERT INTO knowledge_base (question, answer) VALUES
('¿Qué es Petgas?', 'Petgas es una organización que transforma residuos plásticos no reciclables en energía y combustibles limpios mediante tecnología innovadora, con presencia en México y República Dominicana, y que también ha expandido su concepto al sector de criptomonedas.'),
('¿Cuál es la actividad principal de Petgas?', 'Petgas utiliza un proceso avanzado de pirólisis no catalítica e ingeniería inversa en ausencia de oxígeno para convertir plásticos no reciclables en gas, sin emisiones contaminantes, y producir combustibles limpios mediante condensación cuántica.'),
('¿Petgas quema plástico?', 'No, Petgas no quema plástico. Realiza un proceso de gasificación cerrado que no genera emisiones al medio ambiente.'),
('¿En qué países tiene presencia Petgas?', 'Petgas tiene presencia principalmente en México y República Dominicana.'),
('¿Qué hace Petgas en México?', 'En México, Petgas transforma residuos plásticos en energía mediante tecnología de gasificación, ayudando a resolver problemas ambientales relacionados con la contaminación por plásticos.'),
('¿Qué hace Petgas en República Dominicana?', 'En República Dominicana, Petgas promueve esquemas de gestión integral de residuos, revalorización y generación de nuevas cadenas productivas con un modelo de economía circular cerrado, alineado con la estrategia climática del país.'),
('¿Qué es PetgasCoin?', 'PetgasCoin es un token de criptomoneda identificado como $PGC, que opera en redes blockchain como Solana, Polygon y Binance Smart Chain, y forma parte del movimiento ecológico de Petgas.'),
('¿En qué redes blockchain opera PetgasCoin?', 'PetgasCoin opera en Solana, Polygon y Binance Smart Chain.'),
('¿Cuál es el eslogan de PetgasCoin?', 'Join the Petgas movement and transform into a greener tomorrow.'),
('¿Qué modelo económico sigue Petgas?', 'Petgas adopta un modelo de economía circular que elimina residuos plásticos no reciclables, establece alianzas para un futuro sostenible, genera nuevas cadenas productivas y contribuye a la descontaminación del aire, suelo y agua.'),
('¿Cuáles son las etapas del proceso tecnológico de Petgas?', 'El proceso consta de tres etapas: gasificación (conversión del plástico en gas mediante calor controlado), condensación cuántica (separación molecular por longitud de cadenas de carbono) y obtención de combustibles superiores compatibles con cualquier motor.'),
('¿Qué beneficios ambientales ofrece Petgas?', 'Petgas contribuye a eliminar residuos plásticos no reciclables, reduce la contaminación ambiental y produce combustibles limpios sin generar emisiones contaminantes.'),
('¿Qué tipo de plásticos procesa Petgas?', 'Petgas procesa residuos plásticos no reciclables, transformándolos en energía y combustibles limpios.'),
('¿Qué tecnología utiliza Petgas para transformar plásticos?', 'Utiliza pirólisis no catalítica e ingeniería inversa en un proceso cerrado sin oxígeno, seguido de condensación cuántica para obtener combustibles de alta calidad.'),
('¿Qué productos finales obtiene Petgas?', 'Obtiene combustibles limpios y superiores compatibles con cualquier motor, a partir de residuos plásticos no reciclables.');
